import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../Register/RegisterPage.css';
import Navigation from '../Navigation/NavigationPage';
import Footer from '../Footer/FooterPage';
import farmerImage from '../assets/farmer.png';
import customerImage from '../assets/customer.png';
import workerImage from '../assets/worker.png';

function Register() {
  const navigate = useNavigate();
  const [userType, setUserType] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [farmerName, setFarmerName] = useState('');
  const [farmerId, setFarmerId] = useState('');
  const [farmerLicense, setFarmerLicense] = useState(null);
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [addresses, setAddresses] = useState({
    farmerAddress: {
      street: '',
      city: '',
      province: '',
      zipCode: '',
    },
    farmLocationAddress: {
      street: '',
      city: '',
      province: '',
      zipCode: '',
    },
    shippingAddress: {
      street: '',
      city: '',
      province: '',
      zipCode: '',
    },
    communicationAddress: {
      street: '',
      city: '',
      province: '',
      zipCode: '',
    },
  });
  const [workerName, setWorkerName] = useState('');
  const [skills, setSkills] = useState('');
  const [customerName, setCustomerName] = useState('');
  const [error, setError] = useState('');
  const [usernameExistsError, setUsernameExistsError] = useState('');
  const [emailExistsError, setEmailExistsError] = useState('');
  const [phoneExistsError, setPhoneExistsError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [sameAddress, setSameAddress] = useState(true);

  const handleSelectUserType = (selectedUserType) => {
    setUserType(selectedUserType);
    setShowForm(true);
    setSameAddress(true); // Set default to true for Farmer
    clearErrors(); // Clear errors when selecting new userType
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    clearErrorByName(name); // Clear error for the specific field

    if (name === 'phone') {
      const phoneNumber = value.replace(/\D/g, '');
      if (phoneNumber.length === 10) {
        const formattedPhoneNumber = `(${phoneNumber.slice(0, 3)})-${phoneNumber.slice(3, 6)}-${phoneNumber.slice(6, 10)}`;
        setPhone(formattedPhoneNumber);
      } else {
        setPhone(value);
      }
    } else {
      switch (name) {
        case 'username':
          setUsername(value);
          break;
        case 'password':
          setPassword(value);
          break;
        case 'confirmPassword':
          setConfirmPassword(value);
          break;
        case 'farmerName':
          setFarmerName(value);
          break;
        case 'farmerId':
          setFarmerId(value);
          break;
        case 'email':
          setEmail(value);
          break;
        case 'workerName':
          setWorkerName(value);
          break;
        case 'skills':
          setSkills(value);
          break;
        case 'customerName':
          setCustomerName(value);
          break;
        default:
          break;
      }
    }
  };

  const handleAddressChange = (event, addressType) => {
    const { name, value } = event.target;
    setAddresses((prevAddresses) => ({
      ...prevAddresses,
      [addressType]: {
        ...prevAddresses[addressType],
        [name]: value,
      },
    }));
  };

  const handleFileChange = (event) => {
    setFarmerLicense(event.target.files[0]);
  };

  const handleSameAddressChange = () => {
    setSameAddress((prevSameAddress) => !prevSameAddress);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    let requiredFields = ['username', 'password', 'confirmPassword', 'email', 'phone'];
    switch (userType) {
      case 'Farmer':
        requiredFields = [...requiredFields, 'farmerName', 'farmerId', 'farmerLicense'];
        break;
      case 'Worker':
        requiredFields = [...requiredFields, 'workerName', 'skills'];
        break;
      case 'Customer':
        requiredFields = [...requiredFields, 'customerName'];
        break;
      default:
        break;
    }

    const isAnyFieldMissing = requiredFields.some(field => !eval(field));
    if (isAnyFieldMissing) {
      setError('Please fill in all required fields.');
      return;
    }

    if (password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    const formData = new FormData();
    formData.append('userType', userType);
    formData.append('username', username);
    formData.append('password', password);
    formData.append('email', email);
    formData.append('phone', phone);

    for (const addressType in addresses) {
      for (const field in addresses[addressType]) {
        formData.append(`${addressType}.${field}`, addresses[addressType][field]);
      }
    }

    try {
      const response = await fetch('/register', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (response.ok) {
        navigate('/login');
      } else {
        setError(data.message);
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="register-page">
      <Navigation />
      <div className="register-container">
        <h1>Register</h1>
        <div className="user-type-selection">
          <button onClick={() => handleSelectUserType('Farmer')}>
            <img src={farmerImage} alt="Farmer" />
            <span>Farmer</span>
          </button>
          <button onClick={() => handleSelectUserType('Customer')}>
            <img src={customerImage} alt="Customer" />
            <span>Customer</span>
          </button>
          <button onClick={() => handleSelectUserType('Worker')}>
            <img src={workerImage} alt="Worker" />
            <span>Worker</span>
          </button>
        </div>
        {showForm && (
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input type="text" id="username" name="username" value={username} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input type="password" id="password" name="password" value={password} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input type="password" id="confirmPassword" name="confirmPassword" value={confirmPassword} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="email" id="email" name="email" value={email} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label htmlFor="phone">Phone</label>
              <input type="text" id="phone" name="phone" value={phone} onChange={handleChange} required />
            </div>

            {userType === 'Farmer' && (
              <>
                <div className="form-group">
                  <label htmlFor="farmerName">Farmer Name</label>
                  <input type="text" id="farmerName" name="farmerName" value={farmerName} onChange={handleChange} required />
                </div>
                <div className="form-group">
                  <label htmlFor="farmerId">Farmer ID</label>
                  <input type="text" id="farmerId" name="farmerId" value={farmerId} onChange={handleChange} required />
                </div>
                <div className="form-group">
                  <label htmlFor="farmerLicense">Farmer License</label>
                  <input type="file" id="farmerLicense" name="farmerLicense" onChange={handleFileChange} required />
                </div>
              </>
            )}

            {userType === 'Worker' && (
              <>
                <div className="form-group">
                  <label htmlFor="workerName">Worker Name</label>
                  <input type="text" id="workerName" name="workerName" value={workerName} onChange={handleChange} required />
                </div>
                <div className="form-group">
                  <label htmlFor="skills">Skills</label>
                  <input type="text" id="skills" name="skills" value={skills} onChange={handleChange} required />
                </div>
              </>
            )}

            {userType === 'Customer' && (
              <>
                <div className="form-group">
                  <label htmlFor="customerName">Customer Name</label>
                  <input type="text" id="customerName" name="customerName" value={customerName} onChange={handleChange} required />
                </div>
              </>
            )}

            {['Farmer', 'Customer'].includes(userType) && (
              <>
                <div className="form-group">
                  <label>Address</label>
                  <input type="text" name="street" value={addresses[userType.toLowerCase() + 'Address'].street} onChange={(e) => handleAddressChange(e, userType.toLowerCase() + 'Address')} placeholder="Street" required />
                  <input type="text" name="city" value={addresses[userType.toLowerCase() + 'Address'].city} onChange={(e) => handleAddressChange(e, userType.toLowerCase() + 'Address')} placeholder="City" required />
                  <input type="text" name="province" value={addresses[userType.toLowerCase() + 'Address'].province} onChange={(e) => handleAddressChange(e, userType.toLowerCase() + 'Address')} placeholder="Province" required />
                  <input type="text" name="zipCode" value={addresses[userType.toLowerCase() + 'Address'].zipCode} onChange={(e) => handleAddressChange(e, userType.toLowerCase() + 'Address')} placeholder="Zip Code" required />
                </div>
              </>
            )}

            {userType === 'Worker' && (
              <>
                <div className="form-group">
                  <label>Communication Address</label>
                  <input type="text" name="street" value={addresses.communicationAddress.street} onChange={(e) => handleAddressChange(e, 'communicationAddress')} placeholder="Street" required />
                  <input type="text" name="city" value={addresses.communicationAddress.city} onChange={(e) => handleAddressChange(e, 'communicationAddress')} placeholder="City" required />
                  <input type="text" name="province" value={addresses.communicationAddress.province} onChange={(e) => handleAddressChange(e, 'communicationAddress')} placeholder="Province" required />
                  <input type="text" name="zipCode" value={addresses.communicationAddress.zipCode} onChange={(e) => handleAddressChange(e, 'communicationAddress')} placeholder="Zip Code" required />
                </div>
              </>
            )}

            <button type="submit">Register</button>
            {error && <p className="error">{error}</p>}
          </form>
        )}
      </div>
      <Footer />
    </div>
  );
}

export default Register;
