import React from 'react';
import { useLocation } from 'react-router-dom';
import './Checkout.css';
import Navigation from '../Navigation/NavigationPage';
import Footer from '../Footer/FooterPage';
import { useState } from 'react';
const Checkout = ({ cart }) => {
  const [quantity, setQuantity] = useState(1);

  // Function to handle quantity change
  const handleQuantityChange = (index, newQuantity) => {
    const updatedCart = [...cart];
    updatedCart[index].quantity = newQuantity;
    // Assuming you have a function to update cart state in parent component
    updateCart(updatedCart); // Update the cart state in parent component
  };

  // Function to calculate total price
  const calculateTotal = () => {
    let total = 0;
    cart.forEach(item => {
      total += item.price * item.quantity;
    });
    return total;
  };

  // Function to handle payment
  const handlePayment = () => {
    // Implement payment logic here
    console.log('Processing payment...');
  };

  return (
    <div>
      <Navigation />
      <div className="Checkout">
        <h2 className="head">Checkout</h2>
        {cart.length === 0 ? (
          <p>No items in the cart.</p>
        ) : (
          <div>
            <table>
              <thead>
                <tr>
                  <th>Product Name</th>
                  <th>Price</th>
                  <th>Quantity</th>
                  <th>Total</th>
                  <th>Category</th>
                  <th>Sold By</th>
                </tr>
              </thead>
              <tbody>
                {cart.map((item, index) => (
                  <tr key={index}>
                    <td>{item.productName}</td>
                    <td>${item.price.toFixed(2)}</td>
                    <td>
                      <input
                        type="number"
                        min="1"
                        value={item.quantity}
                        onChange={(e) => handleQuantityChange(index, parseInt(e.target.value))}
                      />
                    </td>
                    <td>${(item.price * item.quantity).toFixed(2)}</td>
                    <td>{item.category}</td>
                    <td>{item.username}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div>
              <h3>Total: ${calculateTotal().toFixed(2)}</h3>
              <button onClick={handlePayment}>Proceed to Payment</button>
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default Checkout;
