import React, { Component } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import './Products.css';
import Navigation from '../Navigation/NavigationPage';
import Footer from '../Footer/FooterPage';

class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      cart: [],
      error: ''
    };
  }

  componentDidMount() {
    this.fetchProducts();
  }

  fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:3000/products'); // Update the URL if necessary
      if (response.ok) {
        const products = await response.json();
        const { username } = this.props; // Assuming username is passed as props from authentication

        // Filter products added by the logged-in user
        const userProducts = products.filter(product => product.username === username);
        
        this.setState({ products: userProducts });
      } else {
        console.error('Failed to fetch products');
        this.setState({ error: 'Failed to fetch products' });
      }
    } catch (error) {
      console.error('Error fetching products:', error);
      this.setState({ error: 'Error fetching products' });
    }
  };

  addToCart = (product) => {
    this.setState(prevState => ({
      cart: [...prevState.cart, product]
    }));
  };

  goToCheckout = () => {
    const { navigate } = this.props;
    navigate('/checkout', { state: { cart: this.state.cart } });
  };

  render() {
    const { products, cart, error } = this.state;

    return (
      <div>
        <Navigation />
        <div className="cart-icon">
          <FontAwesomeIcon icon={faShoppingCart} size="2x" />
          <span className="cart-count">{cart.length}</span>
          <button className="checkout-btn" onClick={this.goToCheckout}>Checkout</button>
        </div>
        <div className="Products">
          <h2 className='head'>Your Products</h2>
          {error && <p className="error-message">{error}</p>}
          <div className="product-grid">
            {products.map(product => (
              <div key={product._id} className="product-item">
                <img src={`http://localhost:3000/${product.imagePath}`} alt={product.productName} />
                <div className="product-details">
                  <h3>{product.productName}</h3>
                  <p className="weight">Weight: {product.weight} grams</p>
                  <p className="quantity">Quantity: {product.quantity}</p>
                  <p className="price">Price: ${product.price}</p>
                  <p className="category">Category: {product.category}</p>
                  <p className="username">Added by: {product.username}</p>
                  <button className="add-to-cart-btn" onClick={() => this.addToCart(product)}>Add to Cart</button>
                </div>
              </div>
            ))}
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}

export default Products;
