import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../Register/RegisterPage.css';
import Navigation from '../Navigation/NavigationPage';
import Footer from '../Footer/FooterPage';
import farmerImage from '../assets/farmer.png';
import customerImage from '../assets/customer.png';
import workerImage from '../assets/worker.png';

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userType: '',
      username: '',
      password: '',
      confirmPassword: '',
      farmerName: '',
      farmerId: '',
      farmerLicense: null,
      email: '',
      phone: '',
      addresses: {
        farmerAddress: {
          street: '',
          city: '',
          province: '',
          zipCode: '',
        },
        farmLocationAddress: {
          street: '',
          city: '',
          province: '',
          zipCode: '',
        },
        shippingAddress: {
          street: '',
          city: '',
          province: '',
          zipCode: '',
        },
        communicationAddress: {
          street: '',
          city: '',
          province: '',
          zipCode: '',
        },
      },
      workerName: '', // Worker specific field
      skills: '', // Worker specific field
      customerName: '', // Customer specific field
      error: '',
      usernameExistsError: '',
      emailExistsError: '',
      phoneExistsError: '',
      showForm: false,
      sameAddress: false,
    };
  }

  handleSelectUserType = (userType) => {
    this.setState({ 
      userType,
      showForm: true,
      sameAddress: true, // Set default to true for Farmer
      error: '',  // Clear errors when selecting new userType
      usernameExistsError: '',
      emailExistsError: '',
      phoneExistsError: '',
    });
  };

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  handleAddressChange = (event, addressType) => {
    const { name, value } = event.target;
    this.setState(prevState => ({
      ...prevState,
      addresses: {
        ...prevState.addresses,
        [addressType]: {
          ...prevState.addresses[addressType],
          [name]: value
        }
      }
    }));
  };

  handleFileChange = (event) => {
    this.setState({ farmerLicense: event.target.files[0] });
  };

  handleSameAddressChange = () => {
    this.setState(prevState => ({
      sameAddress: !prevState.sameAddress,
      addresses: {
        ...prevState.addresses,
        farmLocationAddress: { ...prevState.addresses.farmerAddress }
      }
    }));
  };

  handleSubmit = async (event) => {
    event.preventDefault();

    const {
      userType,
      username,
      password,
      confirmPassword,
      farmerName,
      farmerId,
      farmerLicense,
      email,
      phone,
      addresses,
      workerName,
      skills,
      customerName,
    } = this.state;

    if (!username || !password || !confirmPassword || !email || !phone || !customerName) {
      this.setState({ error: 'Please fill in all required fields.' });
      return;
    }

    if (password !== confirmPassword) {
      this.setState({ error: 'Passwords do not match.' });
      return;
    }

    const formData = new FormData();
    formData.append('userType', userType);
    formData.append('username', username);
    formData.append('password', password);
    formData.append('email', email);
    formData.append('phone', phone);

    Object.keys(addresses).forEach(addressType => {
      formData.append(`${addressType}Street`, addresses[addressType].street);
      formData.append(`${addressType}City`, addresses[addressType].city);
      formData.append(`${addressType}Province`, addresses[addressType].province);
      formData.append(`${addressType}ZipCode`, addresses[addressType].zipCode);
    });

    if (userType === 'Farmer') {
      formData.append('farmerName', farmerName);
      formData.append('farmerId', farmerId);
      formData.append('farmerLicense', farmerLicense);
    } else if (userType === 'Worker') {
      formData.append('workerName', workerName);
      formData.append('skills', skills);
    } else if (userType === 'Customer') {
      formData.append('customerName', customerName);
    }

    try {
      const response = await fetch('http://localhost:3000/register', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        alert('Registration successful!');
        this.props.history.push('/login');
      } else {
        if (data.message.includes('Username already exists')) {
          this.setState({ usernameExistsError: 'Username already exists. Please choose a different one.' });
        } else if (data.message.includes('Email already exists')) {
          this.setState({ emailExistsError: 'Email already exists. Please use a different email.' });
        } else if (data.message.includes('Phone Number already exists')) {
          this.setState({ phoneExistsError: 'Phone Number already exists. Please use a different phone number.' });
        } else {
          this.setState({ error: data.message });
        }
      }
    } catch (error) {
      this.setState({ error: 'Failed to register. Please try again later.' });
    }
  };

  goBackToUserTypeSelection = () => {
    this.setState({ 
      showForm: false, 
      userType: '',
      error: '',  // Clear errors when going back
      usernameExistsError: '',
      emailExistsError: '',
      phoneExistsError: '',
    });
  };

  render() {
    const { userType, showForm, error, usernameExistsError, emailExistsError, phoneExistsError, addresses, sameAddress } = this.state;

    return (
      <div>
        <Navigation />
        <div className="register-page">
          {!showForm ? (
            <div>
              <h2 className="userSelectionHeader">Register as</h2>
              <div className="user-type-selection">
                <figure onClick={() => this.handleSelectUserType('Farmer')}>
                  <img src={farmerImage} alt="Farmer" />
                  <figcaption>Farmer</figcaption>
                </figure>
                <figure onClick={() => this.handleSelectUserType('Customer')}>
                  <img src={customerImage} alt="Customer" />
                  <figcaption>Customer</figcaption>
                </figure>
                <figure onClick={() => this.handleSelectUserType('Worker')}>
                  <img src={workerImage} alt="Worker" />
                  <figcaption>Worker</figcaption>
                </figure>
              </div>
            </div>
          ) : (
            <div className="register-form-section">
              <h2 className="userSelectionHeader">Register as {userType}</h2>
              <form onSubmit={this.handleSubmit}>
                <div className="form-columns">
                  <div className="form-column">
                    <div className="form-group">
                      <label>Username:</label>
                      <input type="text" name="username" onChange={this.handleChange} />
                      {usernameExistsError && <p className="error">{usernameExistsError}</p>}
                    </div>
                    <div className="form-group">
                      <label>Password:</label>
                      <input type="password" name="password" onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                      <label>Confirm Password:</label>
                      <input type="password" name="confirmPassword" onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                      <label>Email:</label>
                      <input type="email" name="email" onChange={this.handleChange} />
                      {emailExistsError && <p className="error">{emailExistsError}</p>}
                    </div>
                    <div className="form-group">
                      <label>Phone Number:</label>
                      <input type="tel" name="phone" onChange={this.handleChange} />
                      {phoneExistsError && <p className="error">{phoneExistsError}</p>}
                    </div>
                  </div>

                  <div className="form-column">
                    {userType === 'Farmer' && (
                      <>
                        <div className="form-group">
                          <label>Farmer Name:</label>
                          <input type="text" name="farmerName" onChange={this.handleChange} />
                        </div>
                        <div className="form-group">
                          <label>Farmer ID:</label>
                          <input type="text" name="farmerId" onChange={this.handleChange} />
                        </div>
                        <div className="form-group">
                          <label>Farmer License:</label>
                          <input type="file" name="farmerLicense" onChange={this.handleFileChange} />
                        </div>
                        <div className="form-group">
                          <label>Farmer Address:</label>
                          <input type="text" name="street" placeholder="Street" onChange={(e) => this.handleAddressChange(e, 'farmerAddress')} />
                          <input type="text" name="city" placeholder="City" onChange={(e) => this.handleAddressChange(e, 'farmerAddress')} />
                          <select name="province" onChange={(e) => this.handleAddressChange(e, 'farmerAddress')}>
                            <option value="">Select Province</option>
                            {this.renderProvinceOptions()}
                          </select>
                          <input type="text" name="zipCode" placeholder="Zip Code" onChange={(e) => this.handleAddressChange(e, 'farmerAddress')} />
                        </div>
                        <div className="form-group">
                          <label>Farm Location Address:</label>
                          <input type="text" name="street" placeholder="Street" onChange={(e) => this.handleAddressChange(e, 'farmLocationAddress')} />
                          <input type="text" name="city" placeholder="City" onChange={(e) => this.handleAddressChange(e, 'farmLocationAddress')} />
                          <select name="province" onChange={(e) => this.handleAddressChange(e, 'farmLocationAddress')}>
                            <option value="">Select Province</option>
                            {this.renderProvinceOptions()}
                          </select>
                          <input type="text" name="zipCode" placeholder="Zip Code" onChange={(e) => this.handleAddressChange(e, 'farmLocationAddress')} />
                        </div>
                      </>
                    )}

                    {userType === 'Worker' && (
                      <>
                        <div className="form-group">
                          <label>Worker Name:</label>
                          <input type="text" name="workerName" onChange={this.handleChange} />
                        </div>
                        <div className="form-group">
                          <label>Skills:</label>
                          <input type="text" name="skills" onChange={this.handleChange} />
                        </div>
                      </>
                    )}

                    {userType === 'Customer' && (
                      <>
                        <div className="form-group">
                          <label>Customer Name:</label>
                          <input type="text" name="customerName" onChange={this.handleChange} />
                        </div>
                      </>
                    )}

                    {userType === 'Farmer' && (
                      <div className="form-group">
                        <label>Communication Address:</label>
                        <input type="text" name="street" placeholder="Street" onChange={(e) => this.handleAddressChange(e, 'communicationAddress')} />
                        <input type="text" name="city" placeholder="City" onChange={(e) => this.handleAddressChange(e, 'communicationAddress')} />
                        <select name="province" onChange={(e) => this.handleAddressChange(e, 'communicationAddress')}>
                          <option value="">Select Province</option>
                          {this.renderProvinceOptions()}
                        </select>
                        <input type="text" name="zipCode" placeholder="Zip Code" onChange={(e) => this.handleAddressChange(e, 'communicationAddress')} />
                      </div>
                    )}

                    {userType === 'Farmer' && (
                      <div className="form-group">
                        <label>Shipping Address:</label>
                        <input type="text" name="street" placeholder="Street" onChange={(e) => this.handleAddressChange(e, 'shippingAddress')} />
                        <input type="text" name="city" placeholder="City" onChange={(e) => this.handleAddressChange(e, 'shippingAddress')} />
                        <select name="province" onChange={(e) => this.handleAddressChange(e, 'shippingAddress')}>
                          <option value="">Select Province</option>
                          {this.renderProvinceOptions()}
                        </select>
                        <input type="text" name="zipCode" placeholder="Zip Code" onChange={(e) => this.handleAddressChange(e, 'shippingAddress')} />
                      </div>
                    )}

                    {userType === 'Farmer' && (
                      <div className="form-group">
                        <label>
                          <input type="checkbox" checked={sameAddress} onChange={this.handleSameAddressChange} /> Use same address for Farm Location
                        </label>
                      </div>
                    )}
                  </div>
                </div>

                <div className="form-group form-actions">
                  <button type="submit">Register</button>
                  <button type="button" onClick={this.goBackToUserTypeSelection}>Back</button>
                  {error && <p className="error">{error}</p>}
                </div>
              </form>
            </div>
          )}
        </div>
        <Footer />
      </div>
    );
  }
}

export default Register;
